import { Button, IconButton, makeStyles, Paper, Typography } from '@material-ui/core';
import React from 'react';
import { useEffect } from 'react';
import { useContext } from 'react';
import { useParams } from 'react-router-dom';
import { productContext } from '../../contexts/ProductsContext';

const useStyles = makeStyles(theme => ({
    paper: {
        padding: theme.spacing(2),
        margin: '40px auto',
        maxWidth: 1000,
        height: 'auto'
    }
}))

const ProductDetail = () => {

    const { id } = useParams()
    const { detail, getDetail } = useContext(productContext)
    const classes = useStyles()
    const { deleteProduct, addProductInCart, checkProductInCart } = useContext(productContext)

    useEffect(() => {
        getDetail(id)
    }, [])

    return (
        <Paper elevation={3} style={{backgroundColor:'pink'}} className={classes.paper}>
            <Typography variant='h2' style={{ color:'pink', textAlign: 'center' }}>Detail</Typography>
            {
                detail ? (
                    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <div>

                            <img style={{ width: '500px' }} src={detail.image} alt="" />
                        </div>
                        <div style={{
                            width: '450px',
                            display: 'flex',
                            flexDirection: 'column',
                            alignItems: 'flex-start',
                            justifyContent: 'center'

                        }}>
                            <Typography variant='h3' style={{color:'pink'}} gutterBottom>{detail.title}</Typography>
                            <Typography variant='subtitle1' style={{color:'pink'}} gutterBottom>{detail.type}</Typography>
                            <Typography variant='body1' gutterBottom>{detail.description}</Typography>
                            <Typography variant='h4' style={{color:'pink'}} gutterBottom>{detail.price}</Typography>
                            <Button variant="contained" style={{color:'pink'}} aria-label="share" onClick={() => addProductInCart(id)} color={checkProductInCart(id) ? "pink" : "inherit"}>Buy</Button>
                        </div>
                    </div>

                ) : (<h1>Loading...</h1>)
            }
        </Paper>
    );
};
export default ProductDetail;
