import { useHistory } from 'react-router-dom';

const Header = () => {
  // const [searchVal, setSearchVal] = useState(getSearchVal() || '')
  // const classes = useStyles();
const history = useHistory()
  function getSearchVal() {
    const search = new URLSearchParams(history.location.search)
    return search.get('q')
}


const handleValue = (e) => {
  const search = new URLSearchParams(history.location.search);
  search.set('q', e.target.value);
  history.push(`${history.location.pathname}?${search.toString()}`);
  getProducts();
};

  return (
    <>
    <nav position='fixed' className="navbar navbar-dark navbar-expand-lg fixed-top bg-black-transparent shadow">


      <button
        className="navbar-toggler"
        type="button"
        data-toggle="collapse"
        data-target="#navbarSupportedContent"
        aria-controls="navbarSupportedContent"
        aria-expanded="false"
        aria-label="Toggle navigation"
      >
        <span className="navbar-toggler-icon"></span>
      </button>

      <div className="collapse navbar-collapse" id="navbarSupportedContent">
        <ul className="navbar-nav ml-auto">
          <li className="nav-item active">
            <a className="nav-link" href="/list" style={{color:'pink' }}>
              Shop Now
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="/favorites" style={{color:'pink' }}>
              Favorites
            </a>
          </li>

          <li className="nav-item">
            <a className="nav-link" href="/cart" style={{color:'pink' }}>
              Shopping bag
            </a>
          </li>

          <li className="nav-item">
            <a className="nav-link" href="/register" style={{color:'pink' }}>
              Sign Up or Sign In
            </a>
          </li>
          <input classes={{
                                root: classes.inputRoot,
                                input: classes.inputInput,
                            }} value={searchVal} onChange={(e) =>handleValue(e)} className="search" placeholder="Search..." style={{color:'pink' , borderRadius:'10px' , borderColor:'pink' , width:'100px', height:'30px' , backgroundColor:'transparent' }}/>
        </ul>
      </div>
      <div className="logo" ><a className="navbar-brand" href="/"  style={{color:'pink' , fontWeight:'bold' , fontSize:'36px' , marginRight:'640px'}}>
        Skin Care
      </a></div>
    </nav>

    </>
  );
};

export default Header;
