import { Grid, Button,makeStyles, TextField } from '@material-ui/core';
import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { API} from '../../helpers/constants';
import { useAuth } from '../../contexts/AuthContext';
import CircularProgress from '@material-ui/core/CircularProgress';
import { useMain } from '../../../contexts/ProductContext';
import CommentsCard from './CommentsCard';
const useStyles = makeStyles((theme) => ({
    commentWindow: {
        width: 600,
        height: 300,
        border: 'solid 2px black',
        padding: 10,
        overflow: 'scroll'
    },
    root: {
        display: 'flex',
        '& > * + *': {
          marginLeft: theme.spacing(2),
        },
      },
    input: {
        width: 550
    }
}))
const Comments = () => {
    const classes = useStyles()
    const {currentUser} = useAuth()
    const { getProducts,productDetails, getDetails } = useMain()
    const [value, setValue] = useState('')
    const {id} = useParams()
    const [product, setProduct] = useState('')
    const [comment, setComent] = useState('')
    const handleInp = (e) => {
        let comment = {
            comment: e.target.value,
            user: currentUser.email
        }
        setComment(comment)
        setValue(e.target.value)
    }
    useEffect(()=> {
        getDetails(id)
    },[])
    const addComment = async() => {
        productDetails.comments.push(comment)
        setProduct(productDetails)
        const data = await axios.patch(`${API}/${id}`,product)
        getProducts()
        setValue('')
    }

    // const dar = new Date(date,hours)
    // console.log(dar)
    return (
        <>

        <Grid container justify="center">
            <Grid className={classes.commentWindow}>
            {productDetails.comments ? (productDetails.comments.map((item) => (
                <CommentsCard item={item}/>
            ))
            ) : (
                <div className={classes.root}>
                <CircularProgress color="secondary" />
              </div>
            )}
            </Grid>
            <TextField value={value}className={classes.input} onChange={handleInp}/>
            <Button onClick={() => addComment( product.id)}>Send</Button>
        </Grid>

        </>
    );
};

export default Comments;
